import axios from "axios"

const baseURL = "http://localhost:8080/orders"

export function getOrders(successCB) {
    axios.get(baseURL)
        .then(successCB)
}

export function getPresentOrders() {
    getOrders(orders => {
        let presentOrders = orders.data.filter(order => (order.status !== "Delivered")&&(order.status !== "Cancel"))
        console.log(presentOrders);
    })
}

export function getPastOrders() {
    getOrders(orders => {
        let pastOrders = orders.filter(order => (order.status === "Delivered")||(order.status === "Cancel"))
    })

}
