import { useEffect, useState } from "react"
import { CardGroup } from "react-bootstrap"
import Order from "../components/Order"

function save(id, status) {
    alert(id, status)
}



const PresentOrders = (props) => {

    const [orders, serOrders] = useState([])

    useEffect(() => {
        getOrders(orders => {
            let PRESENT_ORDERS = orders.data.filter(order => (order.status !== "Delivered") && (order.status !== "Cancel"))
            serOrders(PRESENT_ORDERS)
          })
        alert("useeffect of present orders")
    }, [])

    return (
        <div>
            <CardGroup style={{ justifyContent: "center" }}>
                {
                    orders.map((order, index) => {
                        return <Order order={order} onSave={props.onSave} key={index} showSave={true} showStatusDropdown={true} />
                    })
                }
            </CardGroup>
        </div>

    )
}

export default PresentOrders
