import { useEffect, useState } from "react"
import { CardGroup, Spinner } from "react-bootstrap"
import { getOrders } from "../axios/Service"
import Order from "../components/Order"

function save(id, status) {
    alert(id, status)
}



const PresentOrders = (props) => {

    const [orders, setOrders] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        getOrders(orders => {
            let PRESENT_ORDERS = orders.data.filter(order => (order.status !== "Delivered") && (order.status !== "Cancel"))
            setOrders(PRESENT_ORDERS)
            setIsLoading(false)
        })
    }, [])

    if (isLoading) {
        return (
            <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
            </Spinner>
        )
    }
    return (
        <div>
            <CardGroup style={{ justifyContent: "center" }}>
                {
                    orders.map((order, index) => {
                        return <Order order={order} onSave={props.onSave} key={index} showSave={true} showStatusDropdown={true} />
                    })
                }
            </CardGroup>
        </div>

    )
}

export default PresentOrders
