import React, { useEffect, useState } from 'react';
import { Route, Switch } from 'react-router-dom';
import Home from './Home';
import './App.css';
import ComponentA from './components/ComponentA';
import ComponentB from './components/ComponentB';
import MainHeader from './components/MainHeader';
import 'bootstrap/dist/css/bootstrap.min.css'
import PastOrders from './components/PastOrders';
import { getOrders } from './axios/Service';
import PresentOrders from './components/PresentOrders';

const temporders = [{
  "restaurantName": "Sagar Ratna 9",
  "restaurantAddress": "D 56, Jabalpur, Maharashtra",
  "items": [
    {
      "name": "Burger",
      "price": 200,
      "quantity": "2"
    },
    {
      "name": "Pizza",
      "price": 250,
      "quantity": "1"
    },
    {
      "name": "Noodles",
      "price": 100,
      "quantity": "2"
    }
  ],
  "date": "20-08-2021 at 7:05pm",
  "total": 800,
  "status": "Delivered",
  "orderNumber": 70646715400,
  "phoneNumber": 9988776655,
  "address": "Pocket 4, Vivek Vihar, Delhi",
  "id": 9
}]

const App = () => {

  const [orders, setOrders] = useState([])
  const [pastOrders, setpastOrders] = useState([])
  const [presentOrders, setpresentOrders] = useState([])

  useEffect(() => {
    getOrders(orders => {
      let PRESENT_ORDERS = orders.data.filter(order => (order.status !== "Delivered") && (order.status !== "Cancel"))
      let PAST_ORDERS = orders.data.filter(order => (order.status === "Delivered") || (order.status === "Cancel"))
      setOrders(orders.data)
      setpastOrders(PAST_ORDERS)
      setpresentOrders(PRESENT_ORDERS)
    })
  }, [])


  return (
    <div>
      <MainHeader></MainHeader>
      <main>
        <Route path="/past-orders">
          <PastOrders orders={pastOrders}></PastOrders>
        </Route>
        <Route path="/present-orders">
          <PresentOrders onSave={() => alert("save!")}></PresentOrders>
        </Route>
      </main>
    </div>
  )

};

export default App;
