import { useEffect } from "react"
import { CardGroup } from "react-bootstrap"
import { getOrders } from "../axios/Service"
import Order from "./Order"

function save(id, status) {
    alert(id, status)
}


const PastOrders = (props) => {

    const [orders, setOrders] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        getOrders(orders => {
            let PAST_ORDERS = orders.data.filter(order => (order.status === "Delivered") || (order.status === "Cancel"))
            setOrders(PAST_ORDERS)
            setIsLoading(false)
        })
        // alert("useeffect of present orders")
    }, [])

    if (isLoading) {
        return (
            <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
            </Spinner>
        )
    }

    return (

        <CardGroup style={{ justifyContent: "center" }}>
            {
                props.orders.map((order, index) => {
                    return <Order order={order} onSave={save} key={index} showSave={false} showStatusDropdown={false} />
                })
            }
        </CardGroup>
    )
}

export default PastOrders
